import React, { useEffect, useState} from 'react'
import './App.css';
import axios from 'axios'

function App() {
  const [data, setData] = useState('');
  useEffect(() => {
    const fetchData = async () => {
          await axios.get("http://localhost:4000/api/actionChirps")
          .then(res => setData(res.data))
          .catch(err => console.log(err))
    }
     fetchData();
  }, [data]);

  state = { 
    text: '',
  };

  const sendChirp = () => {
    axios.post("http://localhost:4000/api/actionChirps/addChirp")
       .then(res => console.log('Data sent'))
       .catch(err => console.log(err))
  }

  return (
    <div className="App">
      <h1 class="text-center">Hello Handshake!</h1>
      <br/>
      <h3>Chirps</h3>
      <ul class="chirps">
      {data ? data.map(chirp => 
        <li>ID: {chirp.id} -- Text: {chirp.text} -- Upvote: {chirp.upvote}</li>
      ):<li>Loading...</li>}
      </ul>

    <div>
      <form onSubmit={this.sendChirp} method="POST">
        <h3> Enter chirp here! </h3>
        <input type="text" name="newChirp"></input>
        <input type="submit" value="Send" ></input>
      </form>
    </div> 

    </div>
  );
}

export default App;
